<template>  <div id="app">    <!-- <img src="../../assets/logo.png">    <div class="weui-cells"style="color: #b2b2b2;" >        <div class="weui-cell course">            <div class="weui-cell__hd">                <p>课程名称 ：{{ sdText }}</p>            </div>            <div class="weui-cell__bd" id="courseName"></div>        </div>    </div> -->    <div>      <group>       <div class="weui-cell weui-cell_access">            <div class="weui-cell__hd">              <label>头像</label>            </div>            <div class="weui-cell__bd">            </div>            <div class="weui-cell__ft">             <img class="headimg" :src="UserInfo.headImgUrl" />            </div>        </div>        <cell title="昵称" :value="UserInfo.nickName"></cell>        <x-input type="text" title="公司" v-model="UserInfo.company" text-align="right"></x-input>        <x-input type="text" title="姓名" v-model="UserInfo.Contacts" text-align="right"></x-input>        <x-input type="text" title="手机号码" v-model="UserInfo.mobil" text-align="right"></x-input>        <x-address :raw-value="rawvalue" title="所在地区" v-model="UserInfo.address" :list="address"></x-address>        <x-textarea title="地址" :value="adrconts"></x-textarea>      </group>    </div>    <!-- <router-view/> -->  </div></template><!-- <i18n>select address:  zh-CN: 选择地址</i18n> --><script>import {Group, Cell, XInput,XAddress,ChinaAddressV4Data,XTextarea} from 'vux'export default {  name: 'App',  components: {    Group,    Cell,    XInput,    XAddress,    XTextarea  },  data: function () {    return {      himg : 'dd',      rawvalue: true,      UserInfo: [],      value: ['广东省', '广州市', '番禺区'],      address: ChinaAddressV4Data,      adrconts: ''    }      },  //生命周期函数  methods:{        destroy:function(){            this.$destroy()//        }  },  form: {type:'members'},  beforeCreate:function(){      console.log('beforeCreate:刚刚new Vue()之后，这个时候，数据还没有挂载呢，只是一个空壳')                 console.log(this.msg)//undefined      console.log(document.getElementsByClassName("myp")[0])//undefined  },  created:function(){    var that = this    this.$axios.get("/api/weixinPHP/Ashx/View.ashx",{params: {type:'members'}})    .then(res =>{      this.UserInfo = res.data.UserInfo      this.UserInfo.headImgUrl = '/api/' + res.data.UserInfo.headImgUrl      let addressArr = res.data.UserInfo.address.split(' ')      this.UserInfo.address = []      if(addressArr.length > 0){        //adrconts        for(let arr = 0;arr < addressArr.length;arr++){          if(arr < 3){            console.log(addressArr[arr])            this.UserInfo.address.push(addressArr[arr])          }else {            this.adrconts += addressArr[arr]          }        }      }      // this.UserInfo.address = addressArr      console.log(this.UserInfo.address)    }).catch(error =>{      console.log(error)    })      console.log('created:这个时候已经可以使用到数据，也可以更改数据,在这里更改数据不会触发updated函数')      this.msg+='!!!'      console.log('在这里可以在渲染前倒数第二次更改数据的机会，不会触发其他的钩子函数，一般可以在这里做初始数据的获取')      console.log('接下来开始找实例或者组件对应的模板，编译模板为虚拟dom放入到render函数中准备渲染')  },  beforeMount:function(){      console.log('beforeMount：虚拟dom已经创建完成，马上就要渲染,在这里也可以更改数据，不会触发updated')      this.msg+='@@@@'      console.log('在这里可以在渲染前最后一次更改数据的机会，不会触发其他的钩子函数，一般可以在这里做初始数据的获取')      console.log(document.getElementsByClassName("myp")[0])//undefined      console.log('接下来开始render，渲染出真实dom')  },  // render:function(createElement){  //     console.log('render')  //     return createElement('div','hahaha')  // },  mounted:function(){       console.log('mounted：此时，组件已经出现在页面中，数据、真实dom都已经处理好了,事件都已经挂载好了')      console.log(document.getElementsByClassName("myp")[0])      console.log('可以在这里操作真实dom等事情...')      //    this.$options.timer = setInterval(function () {      //        console.log('setInterval')      //         this.msg+='!'        //    }.bind(this),500)  },  beforeUpdate:function(){      //这里不能更改数据，否则会陷入死循环      console.log('beforeUpdate:重新渲染之前触发')      console.log('然后vue的虚拟dom机制会重新构建虚拟dom与上一次的虚拟dom树利用diff算法进行对比之后重新渲染')           },  updated:function(){      //这里不能更改数据，否则会陷入死循环      console.log('updated:数据已经更改完成，dom也重新render完成')  },  beforeDestroy:function(){      console.log('beforeDestory:销毁前执行（$destroy方法被调用的时候就会执行）,一般在这里善后:清除计时器、清除非指令绑定的事件等等...')      // clearInterval(this.$options.timer)  },  destroyed:function(){      console.log('destroyed:组件的数据绑定、监听...都去掉了,只剩下dom空壳，这里也可以善后')  }}</script><style>*, a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video{  padding: 0;  margin:0;  border: 0;  font: inherit;  font-size: .9375rem;  color: #333;}.headimg{width: 2.75rem;height:2.75rem;border-radius: 50%;}.vux-cell-value{color: #333 !important;}/*#app {  font-family: 'Avenir', Helvetica, Arial, sans-serif;  -webkit-font-smoothing: antialiased;  -moz-osx-font-smoothing: grayscale;  text-align: center;  color: #2c3e50;  margin-top: 60px;}*/</style>